import { useEffect } from 'react';
import { useDispatch,useSelector } from 'react-redux';
import { useHistory } from 'react-router-dom';
import { signUpAsync } from '../../store/user/action';
import { Form, Input, Button, Space,Typography } from 'antd';
import TopBar from '../../components/top-bar';
import BottomBar from '../../components/bottom-bar';


const SignUp = () => {
  const dispatch = useDispatch();
  const history = useHistory();
  const{ signUpLoading,signUpError,token } = useSelector(
    (state)=>state.user
  );

  useEffect(()=>{
     if(token){
       history.push("/login");
     }
  },[token]);

  const handleSubmit = (values)=>{
     const {fName,lName,email,password} = values;

     dispatch(loginAsync(fName,lName,email,password));
  }

  return (
    <>
    <TopBar/>

    <Form name="complex-form" initialValues = {{}} onFinish={handleSubmit} labelCol={{ span: 8 }} wrapperCol={{ span: 16 }}
    style={{padding:120}}>
      <Form.Item label="fName">
        <Space>
          <Form.Item
            name="fName"
            noStyle
            rules={[{ required: true, message: 'first name is required' }]}
          >
            <Input style={{ width: 250,fontStyle:'italic' }} placeholder="Please input" />
          </Form.Item>

        </Space>
      </Form.Item>
      <Form.Item label="lName">
        <Space>
          <Form.Item
            name="lName"
            noStyle
            rules={[{ required: true, message: 'last name is required' }]}
          >
            <Input style={{ width: 250,fontStyle:'italic' }} placeholder="Please input" />
          </Form.Item>

        </Space>
      </Form.Item>
      <Form.Item label="Email">

      <Space>
          <Form.Item
            name="email"
            noStyle
            rules={[{ required: true, message: 'Email is required' }]}
          >
            <Input style={{ width: 250,fontStyle:'italic'  }} placeholder="Please input" />
          </Form.Item>

        </Space>

      </Form.Item>

      <Form.Item label="Password">
        <Space>
          <Form.Item
            name="password"
            noStyle
            rules={[{ required: true, message: 'password is required' }]}
          >
            <Input style={{ width: 250,fontStyle:'italic'  }} placeholder="Please input" />
          </Form.Item>

        </Space>
      </Form.Item>

      <Form.Item label=" " colon={false}>
        <Button type="primary" htmlType="submit">
          Register
        </Button>
      </Form.Item>
    </Form>

   <BottomBar/>
    </>
  );
};

export default SignUp;
