import { useEffect } from 'react';
import { useDispatch,useSelector } from 'react-redux';
import { Link,useHistory } from 'react-router-dom';
import { loginAsync } from '../../store/user/action';
import { Form, Input, Button, Space,Typography } from 'antd';
import TopBar from '../../components/top-bar';
import BottomBar from '../../components/bottom-bar';


const Login = () => {
  const dispatch = useDispatch();
  const history = useHistory();
  const{ loginLoading,loginError,token } = useSelector(
    (state)=>state.user
  );

  useEffect(()=>{
     if(token){
       history.push("/messenger");
     }
     history.push("/");
  },[token]);

  const handleSubmit = (values)=>{
     const {email,password} = values;

     dispatch(loginAsync(email,password));
  }

  return (
    <>
    <TopBar/>
    <Form name="complex-form" initialValues = {{}} onFinish={handleSubmit} labelCol={{ span: 8 }} wrapperCol={{ span: 16 }} 
    style = {{padding:180}}>
      <Form.Item label="Email">
        <Space>
          <Form.Item
            name="email"
            noStyle
            rules={[{ required: true, message: 'Username is required' }]}
          >
            <Input style={{ width: 250,fontStyle:'italic' }} placeholder="Please input" />
          </Form.Item>
        </Space>
      </Form.Item>
      <Form.Item label="Password">

      <Space>
          <Form.Item
            name="password"
            noStyle
            rules={[{ required: true, message: 'Password is required' }]}
          >
            <Input style={{ width: 250, fontStyle:'italic' }} placeholder="Please input" />
          </Form.Item>
        </Space>

      </Form.Item>

      <Form.Item label=" " colon={false}>
        <Button type="primary" htmlType="submit">
          SignIn
        </Button>
      </Form.Item>

      <Form.Item>
            <Typography.Text>
              Don't have accout? <Link to="/signup">SignUp</Link>
            </Typography.Text>
          </Form.Item>
    </Form>

   <BottomBar/>
    </>
  );
};

export default Login;
