import { useEffect } from "react";
import { useHistory } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { logOut } from "../../store/user/action";
import BottomBar from "../../components/bottom-bar";
import UserNavBar from "../../components/user_nav";
import { Input,Form,Card } from 'antd';
const { TextArea } = Input;

const Messenger = () => {
  const dispatch = useDispatch();
  const history = useHistory();
  const { loginLoading, loginError, token } = useSelector(
    (state) => state.user
  );

  //  useEffect(() => {
  //   if (token) {
  //     history.push("/");
  //   }
  // }, [token]);

  const onLogOut = () => {
     dispatch(logOut());
     history.push("/login");
  };

  return (
    <>
    <UserNavBar/>



   <Form>
   <TextArea rows={4} placeholder="write some thing" 
   style={{marginLeft:400,marginTop:420,width:400}}/>
   </Form>


   <Form initialValues={{}} onFinish={handleSubmit}>

          <Form.Item
            name="message"
            rules={[{ required: true, message: "Please input your password!" }]}
          >
            <Input.TextArea rows={4} placeholder="write some thing" 
                    style={{marginLeft:400,marginTop:420,width:400}} />
          </Form.Item>

          <Form.Item>
            <div
              style={{
                display: "flex",
                justifyContent: "center",
              }}
            >
              <Button
                type="primary"
                htmlType="submit"
                style={{ width: "200px" }}
                loading={loginLoading}
                disabled={loginLoading}
              >
                Send
              </Button>
            </div>
          </Form.Item>

        </Form>

  <BottomBar/>
    </>
  );
};
export default Messenger;
