import { useDispatch,useSelector } from "react-redux";
import { useHistory } from "react-router-dom";
import BottomBar from "../../components/bottom-bar";
import UserNavBar from "../../components/user_nav";
import { Input,Form,Button} from "antd";
import { createMessageAsync } from "../../store/message/action";


const AddMessage = () => {

  const dispatch = useDispatch();
  const history = useHistory();



  const { createMessageLoading,createMessageErro } = useSelector(
          (state) => state.message);


 const onhandleSubmit = (values) => {

      const { msg,msgFrom,msgTo } = values;

     dispatch(createMessageAsync(msg,msgFrom,msgTo));
     history.push("/messenger");

  };

  return (
    <>
    <UserNavBar/>

    <div id ="messenger" style={{marginLeft:400,marginTop:10,width:500}}>

             <h2>Add Message</h2>

    </div>


   <Form initialValues={{}} onFinish={onhandleSubmit}>

          <Form.Item
            name="message"
          >
            <Input.TextArea rows={4} placeholder="write some thing" 
                    style={{marginLeft:400,marginTop:10,width:400}} />
          </Form.Item>

          <Form.Item
            name="sender"
            rules={[{ required: true}]}
          >
            <Input size="large" placeholder="sender" />
          </Form.Item>

          <Form.Item
            name="receiver"
            rules={[{ required: true}]}
          >
            <Input size="large" placeholder="receiver" />
          </Form.Item>

          <Form.Item>
            <div
              style={{
                display: "flex",
                justifyContent: "center",
              }}
            >
              <Button
                type="primary"
                htmlType="submit"
                style={{ width: "100px",height:50,marginTop:-115,
                marginLeft:450,borderRadius:30,display:"block" }}
              >
                Send
              </Button>
            </div>
          </Form.Item>

        </Form>

  <BottomBar style={{marginTop:-200,position:"absolute"}}/>
    </>
  );
};
export default AddMessage;
