import { useEffect } from "react";
import { useHistory } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { logOut } from "../../store/user/action";
import BottomBar from "../../components/bottom-bar";
import UserNavBar from "../../components/user_nav";
import { Input,Form,Button } from 'antd';
const { TextArea } = Input;

const Messenger = () => {
  const dispatch = useDispatch();
  const history = useHistory();
  const { loginLoading, loginError, token } = useSelector(
    (state) => state.user
  );

  //  useEffect(() => {
  //   if (token) {
  //     history.push("/");
  //   }
  // }, [token]);

  const onLogOut = () => {
     dispatch(logOut());
     history.push("/login");
  };
  const onSend = () => {
    dispatch(logOut());
  }

  return (
    <>
    <UserNavBar/>


   <Form initialValues={{}} onFinish={onSend}>

          <Form.Item
            name="message"
          >
            <Input.TextArea rows={4} placeholder="write some thing" 
                    style={{marginLeft:400,marginTop:420,width:400}} />
          </Form.Item>

          <Form.Item>
            <div
              style={{
                display: "flex",
                justifyContent: "center",
              }}
            >
              <Button
                type="primary"
                htmlType="submit"
                style={{ width: "100px",height:50,marginTop:-115,marginLeft:450,borderRadius:30 }}
                loading={loginLoading}
                disabled={loginLoading}
              >
                Send
              </Button>
            </div>
          </Form.Item>

        </Form>

  <BottomBar style={{marginTop:-50}}/>
    </>
  );
};
export default Messenger;
